            Homework Name: <input type="text" name="homeworkName" onChange={this.handleHomeworkName}/>
            Due Date: <input type="date" name="dueDate" onChange={this.handleDueDate}/>
            <QuestionDescription id={0} questionData={this.problemUpdate}/>
            {
              this.state.problems.map((problem,index) => {
                return (
                  <QuestionDescription key={index} id={index+1} questionData={this.problemUpdate}/>
                )
            })
            }
            <button onClick={this.addAnotherProblem}>Add another problem</button>
            {
              (this.state.problems.length>0) ? 
                <button onClick={this.removeProblem}>Remove</button>
                :
                ""
            }

            <br />
            <button onClick={this.handleHomeworkUploadSubmit}>Submit</button>
        </div>
      );
    }
  }

  export default UploadHomeworkComponent;
