import React from 'react';
import QuestionDescription from './QuestionDescriptionComponent';

class UploadHomeworkComponent extends React.Component {
    constructor() {
      super();
      this.state = {
        problems: []
      }

      this.addAnotherProblem = this.addAnotherProblem.bind(this);
      this.removeProblem = this.removeProblem.bind(this);
    }

    addAnotherProblem() {
      this.setState({ problems: [...this.state.problems, "" ]})
    }

    removeProblem() {
      this.setState({ problem: this.state.problems.splice(0,1)})
    }

    handleHomeworkUploadSubmit() {

    }

    render() {
      return (
        <div>
            <h1>Welcome Professor!</h1>
            Homework Name: <input type="text" name="homeworkName" />
            Due Date: <input type="date" name="dueDate" />
            <QuestionDescription />
            {
              this.state.problems.map((problem,index) => {
                return (
                  <QuestionDescription />
                )
            })
            }
            <button onClick={this.addAnotherProblem}>Add another problem</button>
            {
              (this.state.problems.length>0) ? 
                <button onClick={this.removeProblem}>Remove</button>
                :
                ""
            }

            <br />
            <button onClick={handleHomeworkUploadSubmit}>Submit</button>
        </div>
      );
    }
  }

  export default UploadHomeworkComponent;
