    constructor() {
      super();
      this.state = {
        problems: [],
        homeworkName: '',
        dueDate: '',
        numberOfProblems: 1
      }
    }


    handleHomeworkUploadSubmit(event) {

      var hw_dd = new FormData();
      hw_dd.append("homeworkName", this.state.homeworkName);
