import React from 'react';
import Header from '../HeaderComponent/HeaderComponent';
import './WelcomeScreenComponent.scss';

class WelcomeScreenComponent extends React.Component {
    constructor() {
      super();
    //   this.handleSubmit = this.handleSubmit.bind(this);
    //   this.handleNameChange = this.handleNameChange.bind(this);
    //   this.handleDateChange = this.handleDateChange.bind(this);
    this.handleStudentClick = this.handleStudentClick.bind(this);
    this.handleProfessorClick = this.handleProfessorClick.bind(this);
    this.handleGraderClick = this.handleGraderClick.bind(this);
    }

    handleNameChange(e) {
        this.setState({name: e.target.value});
    }

    handleDateChange(e) {
        this.setState({date: e.target.value});
    }

    handleSubmit() {
    //   event.preventDefault();
      const data = {
          name : this.state.name,
          date : this.state.date
      };

      console.log(data);

      fetch(`http://localhost:8080/create?name=${this.state.name}&date=${this.state.date}`, {
        method: 'POST',
        body: data,
      });
    }

    handleStudentClick() {
        this.props.history.push('/student');
    }

    handleProfessorClick() {
        this.props.history.push('/professor');
    }

    handleGraderClick() {
        this.props.history.push('/grader');
    }


    render() {
      return (
        <div>
            <Header />
            <h1 className="welcome-message">Welcome to the Web grading application</h1>
            <form className="form">
                <button onClick={this.handleStudentClick}>Student</button>
                <button onClick={this.handleProfessorClick}>Professor</button>
                <button onClick={this.handleGraderClick}>Grader</button>
            </form>
        </div>
      );
    }
  }

  export default WelcomeScreenComponent;
