import React from 'react';

class WelcomeScreenComponent extends React.Component {
    constructor() {
      super();
      this.handleSubmit = this.handleSubmit.bind(this);
      this.handleNameChange = this.handleNameChange.bind(this);
      this.handleDateChange = this.handleDateChange.bind(this);
    }

    handleNameChange(e) {
        this.setState({name: e.target.value});
    }

    handleDateChange(e) {
        this.setState({date: e.target.value});
    }

    handleSubmit() {
    //   event.preventDefault();
      const data = {
          name : this.state.name,
          date : this.state.date
      };

      console.log(data);

      fetch(`http://localhost:8080/create?name=${this.state.name}&date=${this.state.date}`, {
        method: 'POST',
        body: data,
      });
    }

    render() {
      return (
        <div>
        <h1>Welcome to the Web grading application</h1>
        <div>
            <input type="button">Student</input>
            <input type="button">Professor</input>
            <input type="button">Grader</input>
        </div>
        </div>
        // <form onSubmit={this.handleSubmit}>
