import React from 'react';
import Header from '../HeaderComponent/HeaderComponent';
import SubHeader from '../SubHeaderComponent/SubHeaderComponent';
import './UploadSolutionComponent.scss';

class UploadSolutionComponent extends React.Component {
    constructor() {
        super();

        this.state = {
            homeworks: ["Homework-1", "Homework-2", "Homework-3"]
        }
    }

    createSelectItems() {
        let items = [];         
        for (let i = 0; i <= this.props.maxValue; i++) {             
            items.push(<option key={i} value={i}>{i}</option>);   
            //here I will be creating my options dynamically based on
            //what props are currently passed to the parent component
        }
        return items;
    }

    onDropdownSelected(e) {
        console.log("THE VAL", e.target.value);
        //here you will see the current selected value of the select input
    }

    render() {
        return (
          <div className='student-container'>
              <Header />
              < SubHeader user="Student"/>
              <h2>Select Homework</h2>
              <div>
                  RIT Username: <input type="text"/>
                  Homework Name: <input type="select" onChange={this.onDropdownSelected} label="Multiple Select" multiple>
                    {this.createSelectItems()}
                    </input>
              </div>
          </div>
        );
    }
}

  export default UploadSolutionComponent;
